CREATE PROCEDURE [dbo].[pro_sw_editRoles]
	@userId INT,
	@roles TINYINT
AS

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_member WITH(NOLOCK);
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT m.f_id, m.f_account, m.f_pwd, m.f_name, m.f_level, m.f_phoneNumber, m.f_email, m.f_accountStatus, w.f_amount, ts.f_totalSpent FROM t_member m WITH (NOLOCK) LEFT JOIN t_wallet w WITH (NOLOCK) ON w.f_memberId = m.f_id LEFT JOIN t_totalSpent ts WITH (NOLOCK) ON ts.f_memberId = m.f_id ORDER BY m.f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END

CREATE PROCEDURE [dbo].[pro_sw_getAllMemberData]
	@pageNumber INT,
    @pageSize INT,
	@beforePagesTotal INT,
	@totalCount INT OUTPUT
AS

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_order WITH(NOLOCK);
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT o.f_id, m.f_account, CONVERT(VARCHAR(16), o.f_createdTime, 20) as f_createdTime, o.f_orderStatus, o.f_deliveryStatus, o.f_deliveryMethod, o.f_total FROM t_order o WITH (NOLOCK) LEFT JOIN t_member m WITH (NOLOCK) ON m.f_id = o.f_memberId ORDER BY o.f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY;
SELECT COUNT(f_deliveryStatus) AS 'statusAll', SUM(CASE WHEN f_deliveryStatus = 1 THEN 1 ELSE 0 END) AS 'status1', SUM(CASE WHEN f_deliveryStatus = 2 THEN 1 ELSE 0 END) AS 'status2', SUM(CASE WHEN f_deliveryStatus = 3 THEN 1 ELSE 0 END) AS 'status3', SUM(CASE WHEN f_deliveryStatus = 4 THEN 1 ELSE 0 END) AS 'status4', SUM(CASE WHEN f_deliveryStatus = 5 THEN 1 ELSE 0 END) AS 'status5', SUM(CASE WHEN f_deliveryStatus = 6 THEN 1 ELSE 0 END) AS 'status6', SUM(CASE WHEN f_orderStatus = 2 THEN 1 ELSE 0 END) AS 'orderStatus2' FROM t_order WITH (NOLOCK); END

CREATE PROCEDURE [dbo].[pro_sw_getAllOrderData]
	@pageNumber INT,
    @pageSize INT,
	@beforePagesTotal INT,
	@totalCount INT OUTPUT
AS

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_product WITH(NOLOCK);
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
IF(@languageNum = 0) BEGIN SELECT f_id, f_nameTW AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceTW AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END ELSE IF(@languageNum = 1) BEGIN SELECT f_id, f_nameEN AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceEN AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END END

CREATE PROCEDURE [dbo].[pro_sw_getAllProductData]
	@pageNumber INT,
    @pageSize INT,
	@beforePagesTotal INT,
	@totalCount INT OUTPUT,
	@languageNum INT
AS

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_userInfo WITH(NOLOCK);
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
-- 使用 OFFSET FETCH 方法來實現分頁 SELECT f_id, f_account, f_roles FROM t_userInfo WITH(NOLOCK) WHERE f_roles > 0 ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; --offset 10 rows ，将前10条记录舍去，fetch next 10 rows only ，向后再读取10条数据。 END

CREATE PROCEDURE [dbo].[pro_sw_getAllUserData]
    @pageNumber INT,
    @pageSize INT,  
	@beforePagesTotal INT,
	@totalCount INT OUTPUT  
AS

BEGIN
    SET NOCOUNT ON;

	SELECT f_id, f_nameTW, f_nameEN, f_stock, f_isOpen, f_warningValue
	FROM t_product WITH(NOLOCK)
	WHERE f_stock <= f_warningValue AND f_isOpen = 1 AND f_warningValue >0;
END

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_order WITH(NOLOCK) WHERE f_deliveryStatus = @deliveryStatusNum;
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT o.f_id, m.f_account, CONVERT(VARCHAR(16), o.f_createdTime, 20) as f_createdTime, o.f_orderStatus, o.f_deliveryStatus, o.f_deliveryMethod, o.f_total FROM t_order o WITH (NOLOCK) LEFT JOIN t_member m WITH (NOLOCK) ON m.f_id = o.f_memberId WHERE o.f_deliveryStatus = @deliveryStatusNum ORDER BY o.f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY;
SELECT COUNT(f_deliveryStatus) AS 'statusAll', SUM(CASE WHEN f_deliveryStatus = 1 THEN 1 ELSE 0 END) AS 'status1', SUM(CASE WHEN f_deliveryStatus = 2 THEN 1 ELSE 0 END) AS 'status2', SUM(CASE WHEN f_deliveryStatus = 3 THEN 1 ELSE 0 END) AS 'status3', SUM(CASE WHEN f_deliveryStatus = 4 THEN 1 ELSE 0 END) AS 'status4', SUM(CASE WHEN f_deliveryStatus = 5 THEN 1 ELSE 0 END) AS 'status5', SUM(CASE WHEN f_deliveryStatus = 6 THEN 1 ELSE 0 END) AS 'status6', SUM(CASE WHEN f_orderStatus = 2 THEN 1 ELSE 0 END) AS 'orderStatus2' FROM t_order WITH (NOLOCK); END

