CREATE PROCEDURE [dbo].[pro_sw_getProductData]
	@productId INT
AS

BEGIN SET NOCOUNT ON;
DECLARE @dbPwd NVARCHAR(64); SELECT @dbPwd = f_pwd FROM t_userInfo WITH(NOLOCK) WHERE f_account = @account COLLATE SQL_Latin1_General_CP1_CS_AS;
IF (@dbPwd IS NOT NULL AND @dbPwd = @pwd COLLATE SQL_Latin1_General_CP1_CS_AS) BEGIN
UPDATE t_userInfo WITH(ROWLOCK) SET f_sessionId = @sessionId WHERE f_account = @account;
SELECT @userId = f_id, @roles = f_roles FROM t_userInfo WITH(NOLOCK) WHERE f_account = @account
SELECT 1; -- 返回登入成功 END ELSE BEGIN SELECT 0; -- 返回登入失敗 END END

CREATE PROCEDURE [dbo].[pro_sw_getPwdAndEditSessionId]
    @account VARCHAR(16),
    @pwd VARCHAR(64),
    @sessionId VARCHAR(24),
	@userId INT OUTPUT,
	@roles TINYINT OUTPUT
AS

BEGIN SET NOCOUNT ON;
SELECT @totalCount = COUNT(f_id) FROM t_order WITH(NOLOCK) WHERE f_orderStatus = 2;
DECLARE @offset INT
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
-- 使用 OFFSET FETCH 方法來實現分頁 SELECT o.f_id, m.f_account, CONVERT(VARCHAR(16), o.f_createdTime, 20) as f_createdTime, o.f_orderStatus, o.f_deliveryStatus, o.f_deliveryMethod, o.f_total FROM t_order o WITH (NOLOCK) LEFT JOIN t_member m WITH (NOLOCK) ON m.f_id = o.f_memberId WHERE o.f_orderStatus = 2 ORDER BY o.f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY;
SELECT COUNT(f_deliveryStatus) AS 'statusAll', SUM(CASE WHEN f_deliveryStatus = 1 THEN 1 ELSE 0 END) AS 'status1', SUM(CASE WHEN f_deliveryStatus = 2 THEN 1 ELSE 0 END) AS 'status2', SUM(CASE WHEN f_deliveryStatus = 3 THEN 1 ELSE 0 END) AS 'status3', SUM(CASE WHEN f_deliveryStatus = 4 THEN 1 ELSE 0 END) AS 'status4', SUM(CASE WHEN f_deliveryStatus = 5 THEN 1 ELSE 0 END) AS 'status5', SUM(CASE WHEN f_deliveryStatus = 6 THEN 1 ELSE 0 END) AS 'status6', SUM(CASE WHEN f_orderStatus = 2 THEN 1 ELSE 0 END) AS 'orderStatus2' FROM t_order WITH (NOLOCK); END

CREATE PROCEDURE [dbo].[pro_sw_getReturnOrderData]
	@pageNumber INT,
    @pageSize INT,
	@beforePagesTotal INT,
	@totalCount INT OUTPUT
AS

BEGIN SET NOCOUNT ON; DECLARE @offset INT
IF(@languageNum = 0) BEGIN IF (@allMinorCategories = 0) BEGIN SELECT @totalCount = COUNT(f_id) FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category = @category) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 100) * 100 + 99) ) AND f_nameTW LIKE '%' + @name + '%';
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT f_id, f_nameTW AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceTW AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category = @category) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 100) * 100 + 99) ) AND f_nameTW LIKE '%' + @name + '%' ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END ELSE BEGIN SELECT @totalCount = COUNT(f_id) FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999 AND f_category % 100 = @category % 100) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999) ) AND f_nameTW LIKE '%' + @name + '%';
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT f_id, f_nameTW AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceTW AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999 AND f_category % 100 = @category % 100) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999) ) AND f_nameTW LIKE '%' + @name + '%' ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END END ELSE IF(@languageNum = 1) BEGIN IF (@allMinorCategories = 0) BEGIN SELECT @totalCount = COUNT(f_id) FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category = @category) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 100) * 100 + 99) ) AND f_nameEN LIKE '%' + @name + '%';
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT f_id, f_nameEN AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceEN AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category = @category) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 100) * 100 + 99) ) AND f_nameEN LIKE '%' + @name + '%' ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END ELSE BEGIN SELECT @totalCount = COUNT(f_id) FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999 AND f_category % 100 = @category % 100) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999) ) AND f_nameEN LIKE '%' + @name + '%';
IF (@beforePagesTotal != CEILING(CAST(@totalCount as float) / @pageSize)) BEGIN SET @offset = 0 END ELSE BEGIN SET @offset = (@pageNumber - 1) * @pageSize END
SELECT f_id, f_nameEN AS f_name, f_category, f_price, f_stock, f_isOpen, f_introduceEN AS f_introduce, f_img, f_warningValue FROM t_product WITH(NOLOCK) WHERE ( (@allBrand = 0 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999 AND f_category % 100 = @category % 100) OR (@allBrand = 1 AND f_category BETWEEN @category AND (@category / 10000) * 10000 + 9999) ) AND f_nameEN LIKE '%' + @name + '%' ORDER BY f_id OFFSET @offset ROWS FETCH NEXT @pageSize ROWS ONLY; END END END

